  List<Place> getHotel (String city) {
    if (city == 'Nicosia') {
      return nicosiaHotels;
    }
    else if (city == 'Limassol') {
      return limassolHotels;
    } else if (city == 'Famagusta') {
      return famagustaHotels;
    } else if (city == 'Paphos') {
      return paphosHotels;
    } else if (city == 'Larnaca') {
      return larnacaHotels;
    }
    else {
      return nicosiaHotels;
  }}


