   ,Place(name: 'Columbia Plaza', imagePath: 'limassolRest/colubia-plaza-12.jpg', city: 'Limassol', lat: 34.67709863007054, long: 33.04798650320846, id: 139, category: 'Restaurant, European Food', description: 'Columbia Plaza in Limassol, Cyprus, is a prominent and multifaceted establishment that stands out as a significant center for both entertainment and dining. Established in November 2008, it has rapidly become a key player in the Cypriot market, catering to a diverse range of needs and preferences.', url: 'https://www.columbiaplaza.com/')
   ,Place(name: 'TGI Fridays - Limassol Marina', imagePath: 'limassolRest/tgi-fridays-limassol.jpg',id: 140, lat: 34.66876147768699, long: 33.04018029132355, city: 'Limassol', category: 'Restaurant, European', description: 'TGI Fridays at Limassol Marina is a popular dining destination known for its American cuisine and bar options. It\'s a great place for both lunch and dinner, offering vegetarian-friendly, vegan, and gluten-free options. The restaurant enjoys a waterfront location at Limassol Marina, providing a scenic view for diners (photo from Tripadvisor).', url: 'https://fridays.com.cy/'),
