  List<Place> nicosiaRest = [
    Place(name: 'Piatsa Gourounaki', imagePath: 'nicosiaRest/piatsa.jpg', lat: 35.17277087992693, long: 33.36245996760972, id: 145, category: 'Restaurant, Greek Food', city: 'Nicosia', description: 'Piatsa Gourounaki in Nicosia is a restaurant known for its Greek and Mediterranean cuisine. It is located on Faneromenis Street. The menu includes a variety of options such as doner kebabs, souvlakia, and meat platters. The restaurant is appreciated for its local cuisine and offers options for different dietary preferences, including vegetarian, vegan, and gluten-free choices. ', url: 'https://piatsaexpress.com/'),
