  List<Place> getRestoraunt(String city) {
    if (city == 'Nicosia') {
      return nicosiaRest;
    }
    else if (city == 'Limassol') {
      return limassolRest;
    } else if (city == 'Famagusta') {
      return famagustaRest;
    } else if (city == 'Paphos') {
      return paphosRest;
    } else if (city == 'Larnaca') {
      return larnacaRest;
    }
    else {
      return nicosiaRest; }
  }


}

