  List<Place> getSight(String city) {
    if (city == 'Nicosia') {
      return nicosiaSights;
    } else if (city == 'Limassol') {
      return limassolSights;
    } else if (city == 'Famagusta') {
      return famagustaSights;
    } else if (city == 'Paphos') {
      return paphosSights;
    } else if (city == 'Larnaca') {
      return larnacaSights;
    } else {
      return nicosiaSights;
    }
  }


